import React from 'react';
import { motion } from 'framer-motion';

const Journey = () => {
  const journeySteps = [
    {
      title: "Identifying the Problem",
      description: "We began by recognizing the critical issue of food wastage in our community and beyond.",
      icon: "🔍"
    },
    {
      title: "Developing Project Focus",
      description: "We narrowed our focus to perishable foods, specifically choosing papaya due to its significant transportation and storage challenges.",
      icon: "🎯"
    },
    {
      title: "Research Phase",
      description: [
        "Studied existing fruit packaging solutions & biodegradable materials",
        "Explored cushioning options including newspaper shreds, coconut husk, and banana leaves",
        "Selected bamboo for its feasibility, cost-effectiveness, and wide availability",
        "Incorporated anti-microbial treatment to prevent microbial growth in sustainable materials"
      ],
      icon: "📚"
    },
    {
      title: "Design & Prototyping",
      description: "Carefully designed and sketched our prototype before moving to the construction phase.",
      icon: "✏️"
    },
    {
      title: "Building the Prototype",
      description: "Constructed the initial prototype and developed the cushioning system.",
      icon: "🛠️"
    },
    {
      title: "Chemical Treatment Development",
      description: "Conducted microbial testing and mass loss analysis, ultimately selecting a 1:1 ratio of lactic and citric acid (1% each).",
      icon: "🧪"
    },
    {
      title: "Business Planning",
      description: "Created a comprehensive business plan and calculated cost per unit for scalability.",
      icon: "💼"
    },
    {
      title: "Product Evaluation",
      description: "Evaluated the product thoroughly, identifying design improvements and cost reduction opportunities.",
      icon: "📊"
    }
  ];

  return (
    <div className="min-h-screen bg-background-light pt-20">
      <section className="py-20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="text-center mb-16"
          >
            <h1 className="text-4xl md:text-5xl font-bold text-primary mb-6">Our Journey</h1>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto">
              From problem identification to solution implementation, follow our journey in developing TheGreenCrate.
            </p>
          </motion.div>

          <div className="relative">
            {/* Timeline line */}
            <div className="absolute left-1/2 transform -translate-x-1/2 h-full w-0.5 bg-primary opacity-20"></div>

            {/* Journey steps */}
            <div className="space-y-20">
              {journeySteps.map((step, index) => (
                <motion.div
                  key={index}
                  initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
                  whileInView={{ opacity: 1, x: 0 }}
                  viewport={{ once: true }}
                  transition={{ duration: 0.6, delay: 0.2 }}
                  className={`relative flex ${
                    index % 2 === 0 ? 'justify-start' : 'justify-end'
                  }`}
                >
                  <div className={`w-full md:w-5/12 ${index % 2 === 0 ? 'pr-8' : 'pl-8'}`}>
                    <div className="bg-white p-6 rounded-lg shadow-md">
                      <div className="text-4xl mb-4">{step.icon}</div>
                      <h3 className="text-xl font-semibold text-primary mb-4">{step.title}</h3>
                      {Array.isArray(step.description) ? (
                        <ul className="list-disc list-inside space-y-2 text-gray-600">
                          {step.description.map((item, i) => (
                            <li key={i}>{item}</li>
                          ))}
                        </ul>
                      ) : (
                        <p className="text-gray-600">{step.description}</p>
                      )}
                    </div>
                  </div>
                  {/* Timeline dot */}
                  <div className="absolute left-1/2 transform -translate-x-1/2 w-4 h-4 bg-primary rounded-full"></div>
                </motion.div>
              ))}
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Journey;
