import React from 'react';
import { motion } from 'framer-motion';

const Home = () => {
  const timelineSteps = [
    {
      title: "Identifying the Problem",
      description: "Understanding food wastage and its impact on global sustainability"
    },
    {
      title: "Developing Focus",
      description: "Narrowing down to perishable foods, specifically papaya due to its transportation and storage challenges"
    },
    {
      title: "Research Phase",
      description: "Comprehensive study of materials and solutions",
      subpoints: [
        "Studied existing fruit packaging solutions & biodegradable materials",
        "Researched cushioning options including newspaper shreds and banana leaves",
        "Selected bamboo for its feasibility, cost-effectiveness, and availability",
        "Added anti-microbial treatment to prevent microbial growth"
      ]
    },
    {
      title: "Design & Prototyping",
      description: "Designing and building the initial prototype"
    },
    {
      title: "Cushioning Development",
      description: "Creating effective cushioning solutions for produce protection"
    },
    {
      title: "Chemical Treatment Research",
      description: "Experimenting with treatments - finalized 1+1% of lactic and citric acid"
    },
    {
      title: "Business Planning",
      description: "Developing cost estimates and business strategy"
    },
    {
      title: "Product Evaluation",
      description: "Identifying design improvements and cost optimization opportunities"
    }
  ];

  const team = [
    { name: "Sparsh Phutela", role: "Coordinator" },
    { name: "Neil Chadha", role: "Team Member" },
    { name: "Sahaj Khurana", role: "Team Member" },
    { name: "Manicka Kumar", role: "Team Member" },
    { name: "Aakarshan Rai", role: "Team Member" },
    { name: "Shivtosh Singh", role: "Team Member" },
    { name: "Aarush Batra", role: "Team Member" },
    { name: "Aditya Jain", role: "Team Member" }
  ];

  return (
    <div className="w-full">
      {/* Hero Section */}
      <section id="hero" className="h-screen relative overflow-hidden gradient-bg flex items-center justify-center">
        <div className="container mx-auto px-4">
          <motion.div 
            className="max-w-4xl mx-auto text-center space-y-12"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            <h2 className="text-6xl md:text-8xl font-bold text-[#4A154B] leading-tight">
              TheGreenCrate
            </h2>
            <p className="text-2xl text-gray-600 max-w-3xl mx-auto">
              A research-driven sustainable solution to reduce post-harvest losses of fresh produce.
            </p>
          </motion.div>
        </div>
      </section>

      {/* About & Mission Section */}
      <section id="about" className="py-20 bg-white">
        <div className="container mx-auto px-4">
          <div className="max-w-4xl mx-auto space-y-16">
            <motion.div 
              className="space-y-8"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
            >
              <h2 className="text-3xl font-bold text-[#4A154B]">About Us</h2>
              <p className="text-lg text-gray-600">
                At BamBox, we create sustainable, antimicrobial packaging for fresh produce. 
                Our bamboo-based solution reduces plastic waste while preventing microbial growth, 
                ensuring food stays fresher for longer.
              </p>
            </motion.div>

            <motion.div 
              className="space-y-8"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
            >
              <h2 className="text-3xl font-bold text-[#4A154B]">Our Mission</h2>
              <p className="text-lg text-gray-600">
                To make eco-friendly packaging practical and commercially viable, combining 
                sustainability, food safety, and cost-efficiency for a greener future.
              </p>
            </motion.div>
          </div>
        </div>
      </section>

      {/* Timeline Section */}
      <section id="timeline" className="py-20 bg-gray-50 relative">
        <div className="absolute inset-0 bg-[radial-gradient(circle_at_top_right,rgba(155,229,100,0.1),transparent_70%)]"></div>
        <div className="container mx-auto px-4 relative">
          <h2 className="text-3xl font-bold text-[#4A154B] text-center mb-16">Our Journey</h2>
          <div className="max-w-5xl mx-auto">
            <div className="absolute left-1/2 top-[100px] bottom-20 w-0.5 bg-[#9BE564]/30"></div>
            {timelineSteps.map((step, index) => (
              <motion.div
                key={index}
                className="relative flex items-center mb-12"
                initial={{ opacity: 0, x: index % 2 === 0 ? -20 : 20 }}
                whileInView={{ opacity: 1, x: 0 }}
                viewport={{ once: true }}
                transition={{ delay: index * 0.1 }}
              >
                <div className={`w-full flex ${index % 2 === 0 ? 'justify-start' : 'justify-end'}`}>
                  <div className={`w-1/2 ${index % 2 === 0 ? 'pr-12' : 'pl-12'}`}>
                    <div className="bg-white p-6 rounded-lg shadow-md border border-gray-100">
                      <h3 className="text-xl font-bold text-[#4A154B] mb-3">{step.title}</h3>
                      <p className="text-gray-600 mb-4">{step.description}</p>
                      {step.subpoints && (
                        <ul className="list-disc list-inside text-gray-600 space-y-2">
                          {step.subpoints.map((point, i) => (
                            <li key={i}>{point}</li>
                          ))}
                        </ul>
                      )}
                    </div>
                  </div>
                </div>
                <div className="absolute left-1/2 transform -translate-x-1/2 -translate-y-1/2">
                  <div className="w-4 h-4 bg-[#9BE564] rounded-full shadow-lg"></div>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Prototype Section */}
      <section className="py-20 bg-white">
        <div className="container mx-auto px-4">
          <div className="max-w-4xl mx-auto">
            <motion.div
              className="space-y-8"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
            >
              <h2 className="text-3xl font-bold text-[#4A154B] text-center mb-8">Our Prototype</h2>
              <div className="bg-gray-50 p-8 rounded-lg">
                <img 
                  src="/prototype.jpg" 
                  alt="TheGreenCrate Prototype" 
                  className="w-full h-auto rounded-lg shadow-lg"
                />
              </div>
            </motion.div>
          </div>
        </div>
      </section>

      {/* Team Section */}
      <section id="team" className="py-20 bg-gray-50">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl font-bold text-[#4A154B] text-center mb-16">The Brains Behind This</h2>
          <div className="max-w-5xl mx-auto grid grid-cols-2 md:grid-cols-4 gap-8">
            {team.map((member, index) => (
              <motion.div
                key={index}
                className="text-center space-y-2"
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ delay: index * 0.1 }}
              >
                <div className="w-24 h-24 mx-auto bg-[#4A154B]/10 rounded-full flex items-center justify-center">
                  <span className="text-2xl text-[#4A154B]">{member.name[0]}</span>
                </div>
                <h3 className="font-medium text-[#4A154B]">{member.name}</h3>
                <p className="text-sm text-gray-600">{member.role}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
